__author__ = 'Jace Xu'
__version__ = "0.6.0"

from AutoIt2Library.keywords._autoit import _AutoIt
from AutoIt2Library.keywords._control import _Control
from AutoIt2Library.keywords._process import _Process
from AutoIt2Library.keywords._win import _Win


class AutoIt2Library(_AutoIt, _Control, _Process, _Win):
    ROBOT_LIBRARY_SCOPE = __author__
    ROBOT_LIBRARY_VERSION = __version__

    waiting_time = 5
    intervals = 0.5
    handle = None

    def set_implicit_wait(self, waiting_time):
        """
        设置隐式等待
        :param waiting_time:
        :return:
        """
        self.waiting_time = float(waiting_time)

    def set_intervals(self, intervals):
        self.intervals = float(intervals)
